import { BASE_API } from "../utils/constants"

export async function getProductsApi(){
    try {
        const url = `${BASE_API}/api/products`;
        const response = await fetch(url);
        const result = await response.json();
        return result;
    } catch (error) {
        throw error;
    }
}

export async function addProductApi(data, token){
    try {
        const formData = new FormData();
        formData.append('title', data.title);
        formaData.append('price', data.price);
        formaData.append('category', data.category);
        formaData.append('active', data.active);
        formaData.append('image', data.image);

        const url = `${BASE_API}/api/products/`
        const params = {
            method: "POST",
            headers:{
                Authorization: `Bearer ${token}`,
            },
            body: formData,
        };
        const response = await fetch(url, params);
        const result = await response.json();
        return result;
    } catch (error) {
        throw error;
    }
}
