  return (
    <div>
        Carrito
        {!products ? (
            <p>Cargando...</p>
        ): size(products) === 0 ? (
            <div style={{textAlign:"center"}}>
                <p>Tu carrito está vacío</p>
                <Link to={`/client/${tableNumber}/orders`}>
                    <Button primary >Ver pedidos</Button>
                </Link>
            </div>
        ):(
            <ListProductCart products={products} />
        )}
    </div>
  )
}
