import React from 'react';
import "./PaymentDetail.scss";
import { Table , Button, Icon } from 'semantic-ui-react';

export function PaymentDetail(props) {
    const {payment, order, openCloseModal, onReloadOrders } = props;
    const getIconPayment = (key) => {
        if(key == "CARD") return "credit card outline";
        if(key == "CASH") return "money bill alternate outline";
    }
  return (
    <div className='payment-detail'>
        <Table striped>
            <Table.Body>
                <Table.Row>
                    <Table.Cell>Mesa: </Table.Cell>
                    <Table.Cell>{payment.table_data.number}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell>Total:</Table.Cell>
                    <Table.Cell>$ {payment.totalPayment}</Table.Cell>
                </Table.Row>
                <Table.Row>
                    <Table.Cell>Forma de pago:</Table.Cell>
                    <Table.Cell>
                        <Icon name={'credit card ouline'} />
                    </Table.Cell>
                </Table.Row>
            </Table.Body>
        </Table>

        <Button primary fluid onClick={() => console.log("Cerrar Mesa")}>
            Marcar como pagado y cerrar mesa
        </Button>
    </div>
  )
}
