import { useState } from "react";
import { createPaymentApi } from "../api/payment";

export function usePayment(){
    const [error, setError] = useState(null)

    const createPayment = async (pay) =>{
        try {
            return await createPaymentApi
        } catch (error) {
            setError(error);
        }
    }
    return{
        error,
        createPayment,
    };
}
