import { useState } from "react";
import { closePaymentApi, createPaymentApi, getPaymentByTableApi } from "../api/payment";

export function usePayment(){
    const [error, setError] = useState(null)

    const createPayment = async (paymentData) =>{
        try {
            return await createPaymentApi(paymentData);
        } catch (error) {
            setError(error);
        }
    }

    const getPaymentByTable = async (idTable) =>{
        try {
            return await getPaymentByTableApi(idTable);
        } catch (error) {
            setError(error);
        }
    }

    const closePayment = async (idPayment) =>{
        try {
            await closePaymentApi
        } catch (error) {
            setError(error);
        }
    }
    return{
        error,
        createPayment,
        getPaymentByTable,
    };
}
