import { useState } from "react";
import { getOrdersByTableApi, checkDeliveredApi, AddOrderToTableApi } from "../api/orders";

export function useOrder(){
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);
    const [orders, setOrders] = useState(null);

    const getOrdersByTable = async (idTable, status, ordering) => {
        try {
            setLoading(true);
            const response = await getOrdersByTableApi(idTable, status, ordering);
            setLoading(false);
            setOrders(response);
        } catch (error) {
            setLoading(false);
            setError(error);
        }
    };

    const checkDeliveredOrder = async (idOrder) =>{
        try {
            await checkDeliveredApi(idOrder);
        } catch (error) {
            setError(error);
        }
    }

    const addOrderToTable= async ()
    return{
        loading,
        error,
        orders,
        getOrdersByTable,
        checkDeliveredOrder,
    };
}
