import React, {useEffect} from 'react';
import {Button, Icon, Container, Link } from "semantic-ui-react";
import { useParams, useNavigate } from "react-router-dom"
import { useTable } from "../../hooks"
import "./ClientLayout.scss";

export function ClientLayout(props) {
    let navigate = useNavigate();
    const { children } = props;
    const { isExistTable } = useTable();
    const { tableNumber } = useParams();

    useEffect(() => {
      (async () =>{
        const exist = await isExistTable(tableNumber);
        if(!exist) closeTable();
        console.log(exist);
      })()
    }, [tableNumber])

    const closeTable = () =>{
      navigate("/");
      
    }
    
  return (
    <div className='client-layout-bg'>
        <Container className='client-layout'>
          <div className='client-layout__header'>
            <Link to="">
              <h1>iCard</h1>
            </Link>
          </div>
          <div className='client-layout__content'>{children}</div>
        </Container>
    </div>
  );
}
