import React from 'react';
import {Form, Button, Checkbox} from "semantic-ui-react";
import {useFormik} from "formik";
import * as Yup from "yup";
import "./AddEditUserForm.scss"
import { functionsIn } from 'lodash';

export function AddEditUserForm() {
    const formik = useFormik({
        initialValues: initialValues(),
        validationSchema: Yup.object(newSchema()),
        validateOnChange: false,
        onSubmit: (formValue) =>{
            console.log("Formulario enviado");
            console.log(formValue);
        },
    });
  return (
    <div>
        <Form className='add-edit-user-form' onSubmit={formik.handleSubmit}>
            <Form.Input 
                name="username" 
                placeholder="Nombre de usuario"
                value={formik.values.username}
                onChange={formik.handleChange}
                error={formik.error.username}
                />
            <Form.Input name="email" 
                type='email' 
                placeholder="Correo Electrónico"
                value={formik.values.email}
                onChange={formik.handleChange}
                error={formik.error.email}
            />
            <Form.Input 
                name="first_name" 
                placeholder="Nombre" 
                value={formik.values.username}
                onChange={formik.handleChange}
                error={formik.error.username}
            />
            <Form.Input 
                name="last_name" 
                placeholder="Apellido"
                value={formik.values.last_name}
                onChange={formik.handleChange}
                error={formik.error.last_name}
                />
            <Form.Input 
            name="password" 
            type="password" 
            placeholder="Contraseña" 
            value={formik.values.password}
            onChange={formik.handleChange}
            error={formik.error.password}
            />
            <div className='add-edit-user-form__active'>
                <Checkbox 
                    toggle 
                    checked={formik.values.is_active} 
                    onChange={(_, data) => console.log(data)}
                /> Usuario activo
            </div>
            <div className='add-edit-user-form__active'>
                <Checkbox toggle /> Usuario administrador
            </div>
            <Button type='submit' primary fluid content="Crear" />
        </Form>
    </div>
  );
}

function initialValues(){
    return  {
        username: "",
        email: "",
        first_name: "",
        last_name: "",
        password: "",
        is_active: true,
        is_staff: false,
    }
}

function newSchema(){
    return{
        username: Yup.string().required(true),
        email: Yup.string().email(true).required(true),
        first_name: Yup.string(),
        last_name: Yup.string(),
        password: Yup.string().required(true),
        is_active: Yup.bool().required(true),
        is_staff: Yup.bool().required(true),
    }
}
