import React, {useEffect, useState} from 'react';
import { size } from 'lodash';
import classNames from 'classnames';
import { Link } from 'react-router-dom';
import "./TableAdmin.scss"
import { Label, Button, Icon, Checkbox } from 'semantic-ui-react';
import { ReactComponent as IcTable} from "../../../../assets/table.svg";
import { getOrdersByTableApi } from '../../../../api/orders';
import { ORDER_STATUS } from '../../../../utils/constants';


export function TableAdmin(props) {
  const {table, reload} = props;
  const [orders, setOrders] = useState([]);
  const [tableBusy, setTableBusy] = useState(false);
  
  useEffect(() => {
    (async  () => {
      const response = await getOrdersByTableApi(table.number, ORDER_STATUS.PENDING);
      setOrders(response);
    })()
  }, [reload]);
  
  useEffect(() => {
    (async  () => {
      const response = await getOrdersByTableApi(table.id, ORDER_STATUS.DELIVERED);
      if(size(response) > 0) setTableBusy(response);
      else setTableBusy(false);
    })()
  }, [reload]);
  
  return (
    <Link className='table-admin' to={`/admin/table/${table.number}`}>
      {size(orders) > 0 ? (
        <Label circular color='orange'>
          {size(orders)}
        </Label>
      ): null}
      <IcTable 
        className={classNames({
          pending: size(orders) > 0,
          busy: tableBusy
        })} 
      />
      <p>Mesa {table.number} </p>
    </Link>
  )
}
