      <div className='add-edit-product-form__active'>
        <Checkbox toggle />
        Producto activo
      </div>
      <Button type='button' fluid >
        Subir Imagen
      </Button>

      <Button type='submit' primary fluid content="Crear" />
    </Form>
  );
}
function formatDropdownData(data){
  return map(data, (item) => ({
    key: item.id,
    text: item.title,
    value: item.id,
  }));
}
