import React, { useEffect, useState } from 'react';
import { Loader } from 'semantic-ui-react';
import { useParams } from 'react-router-dom';
import { forEach, size } from 'lodash';
import { ListOrderAdmin, PaymentDetail } from '../../components/Admin/TableDetails';
import { HeaderPage } from "../../components/Admin";
import { ModalBasic } from "../../components/Common";
import { useOrder, useTable, usePayment } from '../../hooks';
import { number } from 'yup';
import { AddOrderForm } from '../../components/Admin';

export function TableDetailsAdmin() {
  const [reloadOrders, setReloadOrders] = useState(false);
  const [paymentData, setPaymentData] = useState(null);
  const { id } = useParams();
  const {loading, orders, getOrdersByTable, addPaymentToOrder } = useOrder();
  const { table, getTable } = useTable();
  const { createPayment, getPaymentByTable } = usePayment();
 
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    getOrdersByTable(id, "", "ordering=-status,created_at")
  }, [id, reloadOrders]);
  
  useEffect(() => getTable(id), [id]);

  useEffect(() => {
    (async () =>{
      const response = await getPaymentByTable(id);
      if(size(response) > 0) setPaymentData(response[0]);
    })()
  }, [reloadOrders])
  
  
  const onReloadOrders = () => setReloadOrders((prev) => !prev);
  const openCloseModal = () => setShowModal((prev) => !prev);

  const onCreatePayment = async () => {
    const result = window.confirm('Estas seguro de generar la cuenta de la mesa?');
    if(result){
      let totalPayment = 0;
      forEach(orders, (order) =>{
        totalPayment += Number(order.product_data.price);
      })

    const resultTypePayment = window.confirm('¿Quieres pago con tajeta pulsa ACEPTAR, con efectivo CANCELAR?');

      const paymentData = {
        table: id,
        totalPayment: totalPayment.toFixed(2),
        paymentType: resultTypePayment ? "CARD" : "CASH",
        statusPayment: "PENDING",
      };

      const payment = await createPayment(paymentData);

      for await (const order of orders){
        await addPaymentToOrder(order.id, payment.id)
      }
      onReloadOrders();
    }
  }

  return (
    <>
      <HeaderPage 
        title={`Mesa ${table?.number}`} 
        btnTitle={paymentData ? "Ver cuenta" : "Añadir pedido" }
        btnClick={openCloseModal}
        btnTitleTwo={!paymentData ? "Generar cuenta" : null}
        btnClickTwo={onCreatePayment}
      />
      {loading ? (
        <Loader active inline="centered">
          Cargando ...
        </Loader>
      ):(
        <ListOrderAdmin orders={orders} onReloadOrders={onReloadOrders} />
      )}

      <ModalBasic show={showModal} onClose={openCloseModal} title="Generar pedido">
        {paymentData ? (
          <PaymentDetail payment={paymentData} orders={or} />
        ) : (
          <AddOrderForm idTable={id} openCloseModal={openCloseModal} onReloadOrders={onReloadOrders}/>
        )}
      </ModalBasic>
    </>
  )
}
